INSERT INTO `script_texts` VALUES ('-1609392', 'This is the beginning AND the end, mortals. None may enter the master\'s sanctum!', '', '', '', '', '', '', '', '', '16950', '1', '0', '0', 'intro');
INSERT INTO `script_texts` VALUES ('-1609393', 'The Scourge will wash over this world as a swarm of death and destruction!', '', '', '', '', '', '', '', '', '16941', '1', '0', '0', 'aggro');
INSERT INTO `script_texts` VALUES ('-1609394', 'BONE STORM!', '', '', '', '', '', '', '', '', '16946', '1', '0', '0', 'bone_storm');
INSERT INTO `script_texts` VALUES ('-1609395', 'Bound by bone!', '', '', '', '', '', '', '', '', '16947', '1', '0', '0', 'bone_spike1');
INSERT INTO `script_texts` VALUES ('-1609396', 'Stick Around!', '', '', '', '', '', '', '', '', '16948', '1', '0', '0', 'bone_spike2');
INSERT INTO `script_texts` VALUES ('-1609397', 'The only escape is death!', '', '', '', '', '', '', '', '', '16949', '1', '0', '0', 'bone_spike3');
INSERT INTO `script_texts` VALUES ('-1609398', 'More bones for the offering!', '', '', '', '', '', '', '', '', '16942', '1', '0', '0', 'slay1');
INSERT INTO `script_texts` VALUES ('-1609399', 'Languish in damnation!', '', '', '', '', '', '', '', '', '16943', '1', '0', '0', 'slay2');
INSERT INTO `script_texts` VALUES ('-1609400', 'I see... only darkness...', '', '', '', '', '', '', '', '', '16944', '1', '0', '0', 'death');
INSERT INTO `script_texts` VALUES ('-1609401', 'THE MASTER\'S RAGE COURSES THROUGH ME!', '', '', '', '', '', '', '', '', '16945', '1', '0', '0', 'berserk');

-- Lady Deathwhisper
INSERT INTO `script_texts` VALUES ('-1609402', 'Fix your eyes upon your crude hands! The sinew, the soft meat, the dark blood coursing within!', '', '', '', '', '', '', '', '', '16878', '1', '0', '0', 'speech3');
INSERT INTO `script_texts` VALUES ('-1609403', 'It is a weakness! A crippling flaw! A joke played by the creators upon their own creations!', '', '', '', '', '', '', '', '', '17268', '1', '0', '0', 'speech4');
INSERT INTO `script_texts` VALUES ('-1609404', 'The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it!', '', '', '', '', '', '', '', '', '17269', '1', '0', '0', 'speech5');
INSERT INTO `script_texts` VALUES ('-1609405', 'Through our master all things are possible! His power is without limit, and his will unbending!', '', '', '', '', '', '', '', '', '17270', '1', '0', '0', 'speech6');
INSERT INTO `script_texts` VALUES ('-1609406', 'Those who oppose him will be destroyed utterly! And those who serve, who serve wholly, unquestioningly, with utter devotion of mind and soul? Elevated! To heights beyond your ken!', '', '', '', '', '', '', '', '', '17271', '1', '0', '0', 'speech7');
INSERT INTO `script_texts` VALUES ('-1609407', 'You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness!', '', '', '', '', '', '', '', '', '17272', '1', '0', '0', 'speech1');
INSERT INTO `script_texts` VALUES ('-1609408', 'You can see through the fog that hangs over this world like a shroud and grasp where true power lies!', '', '', '', '', '', '', '', '', '17273', '1', '0', '0', 'speech2');
INSERT INTO `script_texts` VALUES ('-1609409', 'What is this disturbance?! You dare trespass upon this hallowed ground? This shall be your final resting place.', '', '', '', '', '', '', '', '', '16868', '1', '0', '0', 'aggro');
INSERT INTO `script_texts` VALUES ('-1609410', 'Enough! I see I must take matters into my own hands!', '', '', '', '', '', '', '', '', '16877', '1', '0', '0', 'phase_two');
INSERT INTO `script_texts` VALUES ('-1609411', 'Take this blessing and show these intruders a taste of our master\'s power.', '', '', '', '', '', '', '', '', '16873', '1', '0', '0', 'dark_empowerment');
INSERT INTO `script_texts` VALUES ('-1609412', 'I release you from the curse of flesh!', '', '', '', '', '', '', '', '', '16874', '1', '0', '0', 'dark_transformation');
INSERT INTO `script_texts` VALUES ('-1609413', 'Arise and exalt in your pure form!', '', '', '', '', '', '', '', '', '16875', '1', '0', '0', 'animate_dead');
INSERT INTO `script_texts` VALUES ('-1609414', 'You are weak, powerless to resist my will!', '', '', '', '', '', '', '', '', '16876', '1', '0', '0', 'dominate_mind');
INSERT INTO `script_texts` VALUES ('-1609415', 'This charade has gone on long enough.', '', '', '', '', '', '', '', '', '16872', '1', '0', '0', 'berserk');
INSERT INTO `script_texts` VALUES ('-1609416', 'All part of the masters plan! Your end is... inevitable!', '', '', '', '', '', '', '', '', '16871', '1', '0', '0', 'death');
INSERT INTO `script_texts` VALUES ('-1609417', 'Do you yet grasp of the futility of your actions?', '', '', '', '', '', '', '', '', '16869', '1', '0', '0', 'slay1');
INSERT INTO `script_texts` VALUES ('-1609418', 'Embrace the darkness... Darkness eternal!', '', '', '', '', '', '', '', '', '16870', '1', '0', '0', 'slay2');

